import React from 'react';
import {Text, View, Button, FlatList, StyleSheet, Image, SafeAreaView, Dimensions} from 'react-native';
import Hackerrank from './images/img_hakerrank.png';


const SitesContainer=({data})=>{

    return(
    <View style={styles.itemContainer}>
        <Image style={styles.image} source={data.source}></Image>
        <Text style={styles.imageText}>{data.name}</Text>
    </View>  
    )  

}

const DATA = [
    {   
        id:"1",
        name:"Hackerrank",
        source:Hackerrank,
        url:"www.hackerrank.com",
    },
    {   
        id:"2",
        name:"Hackerearth",
        source:Hackerrank,
        url:"www.hackerearth.com",
    },
    {   
        id:"3",
        name:"Codechef",
        source:Hackerrank,
        url:"www.codechef.com",
    },
    {   
        id:"4",
        name:"Geeks For Geeks",
        source:Hackerrank,
        url:"www.geeksforgeeks.com",
    },
]

class SiteScreen extends React.Component{

    constructor(props){
        super(props);
    }
    render()
    {
        return(
            <SafeAreaView style={styles.container}>
                <FlatList
                data={DATA}
                renderItem={({ item }) => <SitesContainer data={item} />}
                keyExtractor={item => item.id}
                horizontal={true}
                />
            </SafeAreaView>     
            );
    }
}

const styles = StyleSheet.create({
    container:{
        display:'flex',
        flex:1,
        alignItems:'center',
        justifyContent:'center',
    },
    itemContainer:{
        display:'flex',
        alignItems:'center',
        justifyContent:'center',
        padding:10,
        width:Math.round(Dimensions.get('window').width),
    },
    image:{
        height:250,
        width:250,
        resizeMode:'contain',
    },
    imageText:{
        fontSize:40,
        fontFamily:'monospace',
    }
})




export default SiteScreen;
