import React from 'react';
import {Text, View, Button, FlatList, StyleSheet, Image, SafeAreaView} from 'react-native';
import Hackerrank from './images/img_hakerrank.png';


const SitesContainer=(data)=>{

    return(
    <View style={styles.itemContainer}>
        <Image style={styles.image} source={data.source}></Image>
    <Text style={styles.imageText}>{data}</Text>
    </View>  
    )  

}

const DATA = (
    {   
        id:"1",
        name:"Hackerrank",
        source:Hackerrank,
        url:"www.hackerrank.com",
    }
)

class SiteScreen extends React.Component{

    constructor(props){
        super(props);
    }
    render()
    {
        return(
                
                <SafeAreaView>
                    <FlatList
                        data={DATA}
                        renderItem={({ item }) => <SitesContainer data={item.name}/>}
                        keyExtractor={item => item.id}
                    />

                </SafeAreaView>  
                 
            );
    }
}

const styles = StyleSheet.create({
    container:{
        display:'flex',
        flex:1,
        alignItems:'center',
        justifyContent:'center',
    },
    itemContainer:{
        display:'flex',
        alignItems:'center',
        justifyContent:'center',  
    },
    image:{
        height:250,
        width:250,
        resizeMode:'contain',
    },
    imageText:{
        fontSize:40,
        fontFamily:'monospace',
    }
})




export default SiteScreen;
