import React from 'react';
import {Text, View, StyleSheet, Dimensions, Image} from 'react-native';
import {Navbar} from '../components/Navbar';
import Homeimg from '../assets/images/img_home.jpg'


class Home extends React.Component
{
    constructor(props)
    {
        super(props);
    }

    render()
    {
        return(
            <View style={styles.container}>
                 <Navbar toggleHandler={this.props.navigation.openDrawer} content="Home"/>
                 <View style={styles.itemContainer}>
                    <Image style={styles.image} source={Homeimg}></Image>
                </View>  
                 <View style={styles.textContainer} >
                     <Text style={styles.text}>Compkit is a collection of the trending technologies and tools 
                     that every programmer should know .Competitive Coding websites, Web devlopment frameworks, Mobile Development , Databases , Editors 
                    various plugins and useful tools have been covered .It also has an interesting Quotes section 
                    and resourceful git repositories .</Text>
                 </View>
            </View>
        )
    }

}

const styles =  StyleSheet.create({
    container:{
        display:'flex',
        flex:1,    
    },
    text:{
        fontSize:20,   
        textAlign:'justify', 
    },
    textContainer:{
        margin:3,
    },
    itemContainer:{
        
        width:Math.round(Dimensions.get('window').width), 

    },
    image:{
        height:300,
        width:Math.round(Dimensions.get('window').width),
        resizeMode:'cover',
        
    },
})

export default Home;
