import React from 'react';
import {Text, View, Button, FlatList, StyleSheet, Image, SafeAreaView, Dimensions} from 'react-native';
import {CompetitiveList} from './Data.js'


const SitesContainer=(props)=>{

    return(
    <View style={styles.itemContainer}>
        <Image style={styles.image} source={props.data.source}></Image>
        <Text style={styles.imageText}>{props.data.name}</Text>
    </View>  
    )  

}

const HorizontalList=(props)=>{
    return(
        <FlatList
        data={props.data}
        renderItem={({ item }) => <SitesContainer data={item} />}
        keyExtractor={item => item.id}
        horizontal={true}
        />
    )
}

class SiteScreen extends React.Component{

    constructor(props){
        super(props);
    }
    render()
    {
        return(
            <SafeAreaView style={styles.container}>
               <HorizontalList data={CompetitiveList}/>
            </SafeAreaView>     
            );
    }
}

const styles = StyleSheet.create({
    container:{
        display:'flex',
        flex:1,
        alignItems:'center',
        justifyContent:'center',
    },
    itemContainer:{
        display:'flex',
        alignItems:'center',
        justifyContent:'center',
        padding:10,
        width:Math.round(Dimensions.get('window').width),
    },
    image:{
        height:250,
        width:250,
        resizeMode:'contain',
    },
    imageText:{
        fontSize:40,
        fontFamily:'monospace',
    }
})




export default SiteScreen;
